    PVector vTarget = new PVector(mouseX, mouseY);
    PVector vDir = PVector.sub(vTarget, vLoc);
    if(vDir.mag() &gt; r) {
        vDir.normalize();
        vDir.mult(1.23);
        vLoc.add(vDir);
    }
    vDir.normalize();
    vDir.mult(r);
    tx = vDir.x;
    ty = vDir.y;
}

void show() {
    circle(vLoc.x, vLoc.y, r*2);
    line(vLoc.x, vLoc.y, vLoc.x +tx, vLoc.y +ty);
}
